@import '../../../../static/custom.less';
@header4: header4;
.@{header4}-wrapper {
  min-height: 720px;
  .@{header4} {
    >h1,
    >p {
      text-align: center;
    }
    &-tag {
      & i {
        width: 12px;
        height: 14px;
        display: inline-block;
        vertical-align: middle;
        margin-right: 5px;
      }
      &-name {
        display: inline-block;
      }
    }
    .ant-tabs-bar {
      text-align: center;
    }
    .ant-tabs {
      .ant-tabs-nav {
        float: none;
        text-align: center;
      }
    }
    &-tabs-wrapper {
      position: relative;
      margin-top: 24px;
    }
    &-content {
      display: flex;
      align-items: center;
    }
    &-text {
      padding: 24px 48px;
    }
    &-img {
      padding: 24px 48px;
    }
    .ant-tabs-tabpane {
      margin-top: 40px;
    }
  }
}

@media screen and (max-width: 767px) {
  .@{header4}-wrapper {
    min-height: 980px;
    overflow: hidden;
    .@{header4} {
      max-width: 100%;
      &-content {
        display: block;
      }
      &-text,
      &-img {
        text-align: left;
        padding: 0;
      }
      &-img {
        margin-top: 32px;
      }
      .ant-tabs-bar {
        width: auto;
        .ant-tabs-nav {
          float: left;
        }
      }
    }
  }
}

Pricing0.templateStr:
import React from 'react';
import OverPack from 'rc-scroll-anim/lib/ScrollOverPack';
import QueueAnim from 'rc-queue-anim';
import TweenOne from 'rc-tween-one';
import { Row, Col } from 'antd';
/* replace-start-value = import { getChildrenToRender } from './utils'; */
import { getChildrenToRender } from '../../../utils';
/* replace-end-value */
/* replace-start */
import './index.less';
/* replace-end */

function Pricing0(props) {
  const { ...tagProps } = props;
  const { dataSource, isMobile } = tagProps;
  delete tagProps.dataSource;
  delete tagProps.isMobile;
  const animType = {
    queue: isMobile ? 'bottom' : 'right',
    one: isMobile ? {
      scaleY: '+=0.3', opacity: 0, type: 'from', ease: 'easeOutQuad',
    }
      : {
        x: '-=30', opacity: 0, type: 'from', ease: 'easeOutQuad',
      },
  };
  return (
    <div
      {...tagProps}
      {...dataSource.wrapper}
    >
      <OverPack
        component={Row}
        {...dataSource.OverPack}
        /* replace-start */
        data-edit="Row,OverPack"
      /* replace-end */
      >
        <TweenOne
          key="img"
          animation={animType.one}
          resetStyle
          {...dataSource.imgWrapper}
          component={Col}
          componentProps={{ md: dataSource.imgWrapper.md, xs: dataSource.imgWrapper.xs }}
          /* replace-start */
          data-edit="Col"
        /* replace-end */
        >
          <span
            {...dataSource.img}
          >
            <img src={dataSource.img.children} width="100%" alt="img" />
          </span>
        </TweenOne>
        <QueueAnim
          key="text"
          type={animType.queue}
          leaveReverse
          ease={['easeOutQuad', 'easeInQuad']}
          {...dataSource.childWrapper}
          component={Col}
          componentProps={{ md: dataSource.childWrapper.md, xs: dataSource.childWrapper.xs }}
          /* replace-start */
          data-edit="Col,childWrapper"
        /* replace-end */
        >
          {
            dataSource.childWrapper.children.map(getChildrenToRender)
          }
        </QueueAnim>
      </OverPack>
    </div>
  );
}

export default Pricing0;

Pricing0.less:
@import '../../../../static/custom.less';
@pricing0: pricing0;
.@{pricing0}-wrapper {
  .@{pricing0} {
    min-height: 370px;
    padding: 0 24px;
    display: flex;
    align-items: flex-end;
    &-img-wrapper {
      height: 100%;
      transform-origin: top;
      padding: 0 32px;
      .@{pricing0}-img {
        display: block;
        width: 100%;
        max-width: 560px;
        img {
          display: block;
        }
      }
    }
    &-text-wrapper {
      min-height: 320px;
      padding: 0 40px;
      max-width: 560px;
      margin-bottom: 32px;
      .@{pricing0}-content,
      .@{pricing0}-title {
        position: relative !important;
      }
      .@{pricing0}-title {
        font-size: 24px;
        font-weight: normal;
        color: #404040;
        margin: 72px auto 16px;
      }
      .@{pricing0}-content {
        font-size: 12px;
        color: #666;
        line-height: 1.5;
      }
      .@{pricing0}-pricing {
        font-size: 36px;
        color: #404040;
        margin: 32px 0 24px;
      }
    }
  }
}

@media screen and (max-width: 767px) {
  .@{pricing0}-wrapper {
    min-height: 720px;
    .@{pricing0} {
      display: block;
      &-img-wrapper {
        padding: 0;
        text-align: center;
        margin-top: 24px;
        .@{pricing0}-img {
          display: inline-block;
          width: 80%;
          margin: auto;
        }
      }
      &-text-wrapper {
        height: auto;
        text-align: center;
        padding: 0;
        max-width: 100%;
        .@{pricing0}-content,
        .@{pricing0}-title {
          width: 100%;
          top: auto;
        }
        .@{pricing0}-title {
          margin: 32px auto 16px;
          font-size: 24px;
        }
      }
    }
  }
}

Pricing1.templateStr:
import React from 'react';
import OverPack from 'rc-scroll-anim/lib/ScrollOverPack';
import QueueAnim from 'rc-queue-anim';
import { Row, Col, Button } from 'antd';
/* replace-start-value = import { getChildrenToRender } from './utils'; */
import { getChildrenToRender } from '../../../utils';
/* replace-end-value */
/* replace-start */
import './index.less';
/* replace-end */

class Pricing1 extends React.PureComponent {
  getChildrenToRender = (item) => {
    const {
      wrapper,
      topWrapper,
      name,
      buttonWrapper,
      line,
      content,
      money,
    } = item.children;
    return (
      <Col
        key={item.name}
        {...item}
        /* replace-start */
        data-edit="Col"
        /* replace-end */
      >
        <QueueAnim type="bottom" {...wrapper}>
          <div {...topWrapper}>
            <div {...name} key="name">
              {
                /* replace-start-value = name.children */
                React.createElement('span', {
                  dangerouslySetInnerHTML: { __html: name.children },
                })
                /* replace-end-value */
              }
            </div>
            <h1 {...money} key="money">
              {
                /* replace-start-value = money.children */
                React.createElement('span', {
                  dangerouslySetInnerHTML: { __html: money.children },
                })
                /* replace-end-value */
              }
            </h1>
          </div>
          <div {...content} key="content">
            {
              /* replace-start-value = content.children */
              React.createElement('span', {
                dangerouslySetInnerHTML: { __html: content.children },
              })
              /* replace-end-value */
            }
          </div>
          <i {...line} key="line" />
          <div {...buttonWrapper} key="button">
            <Button
              {...buttonWrapper.children.a}
              /* replace-start */
              data-edit="link,text"
              /* replace-end */
            >
              {
                /* replace-start-value = buttonWrapper.children.a.children */
                React.createElement('span', {
                  dangerouslySetInnerHTML: { __html: buttonWrapper.children.a.children },
                })
                /* replace-end-value */
              }
            </Button>
          </div>
        </QueueAnim>
      </Col>
    );
  };

  render() {
    const { ...props } = this.props;
