                type="bottom"
                key="blockTop"
                {...dataSource.blockTop}
                component={Row}
                /* replace-start */
                data-edit="Row"
              /* replace-end */
              >
                {listTopChildren}
              </QueueAnim>
              <Divider key="divider" />
              <QueueAnim
