}
export default Banner;

Banner1.templateStr:
import React from 'react';
import { Button } from 'antd';
import {
  DownOutlined,
} from '@ant-design/icons';
import QueueAnim from 'rc-queue-anim';
import TweenOne, { TweenOneGroup } from 'rc-tween-one';
import BannerAnim, { Element } from 'rc-banner-anim';
/* replace-start-value = import { isImg } from './utils'; */
import { polyfill } from 'react-lifecycles-compat';
import { isImg } from '../../../../../share/utils';
/* replace-end-value */
import 'rc-banner-anim/assets/index.css';
/* replace-start */
import './index.less';
/* replace-end */
const { BgElement } = Element;
class Banner extends React.PureComponent {
  /* replace-start */
  static getDerivedStateFromProps(props, { prevProps, $self, current: prevCurrent }) {
    const { func } = props;
    const nextState = {
      prevProps: props,
    };
    if (prevProps && props !== prevProps) {
      const childLen = props.dataSource.BannerAnim.children.length;
      if (func) {
        const current = func.currentPage > childLen ? childLen : func.currentPage;
        if ($self.banner) {
          $self.banner.slickGoTo(current - 1);
        }
        nextState.current = current;
      } else if (prevCurrent > childLen && $self.banner) {
        $self.banner.slickGoTo(childLen - 1);
        nextState.current = childLen;
      }
    }
    return nextState;
  }

  constructor(props) {
    super(props);
    this.state = {
      current: props.func ? props.func.currentPage : 1,
      $self: this,// eslint-disable-line
    };
  }

  /* replace-end */
  render() {
    const { ...props } = this.props;
    const { dataSource } = props;
    delete props.dataSource;
    delete props.isMobile;
    const childrenToRender = dataSource.BannerAnim.children.map((item, i) => {
      const elem = item.BannerElement;
      const elemClassName = elem.className;
      delete elem.className;
      const { bg, textWrapper, title, content, button } = item;
      return (
        <Element
          key={i.toString()}
          {...elem}
          prefixCls={elemClassName}
        >
          <BgElement
            key="bg"
            {...bg}
          />
          <QueueAnim
            type={['bottom', 'top']}
            delay={200}
            key="text"
            {...textWrapper}
          >
            <div
              key="logo"
              {...title}
              /* replace-start */
              data-edit="text,image"
            /* replace-end */
            >
              {
                typeof title.children === 'string' && title.children.match(isImg) ? (
                  <img src={title.children} width="100%" alt="img" />
                ) : /* replace-start-value = title.children */React.createElement('span', { dangerouslySetInnerHTML: { __html: title.children } })
                /* replace-end-value */
              }
            </div>
            <div
              key="content"
              {...content}
            >
              {
                /* replace-start-value = content.children */
                React.createElement('span', { dangerouslySetInnerHTML: { __html: content.children } })
                /* replace-end-value */
              }
            </div>
            <Button
              ghost
              key="button"
              {...button}
              /* replace-start */
              data-edit="link,text"
            /* replace-end */
            >
              {
                /* replace-start-value = button.children */
                React.createElement('span', { dangerouslySetInnerHTML: { __html: button.children } })
                /* replace-end-value */
              }
            </Button>
          </QueueAnim>
        </Element>
      );
    });
    return (
      <div
        {...props}
        {...dataSource.wrapper}
        /* replace-start */
        data-comp={[`banner-switch={ "current": ${this.state.current}, "total": ${dataSource.BannerAnim.children.length
          } ,"childRoute": ["BannerAnim"] }`]}
      /* replace-end */
      >
        <TweenOneGroup
          key="bannerGroup"
          enter={{ opacity: 0, type: 'from' }}
          leave={{ opacity: 0 }}
          component=""
        >
          <div className="banner1-wrapper" key="wrapper">
            <BannerAnim
              key="BannerAnim"
              {...dataSource.BannerAnim}
              /* replace-start */
              ref={(c) => {
                this.banner = c;
              }}
              initShow={this.state.current - 1}
            /* replace-end */
            >
              {childrenToRender}
            </BannerAnim>
          </div>
        </TweenOneGroup>
        <TweenOne
          animation={{
            y: '-=20', yoyo: true, repeat: -1, duration: 1000,
          }}
          className="banner1-icon"
          style={{ bottom: 40 }}
          key="icon"
        >
          <DownOutlined />
        </TweenOne>
      </div>
    );
  }
}

/* replace-start-value = export default Banner */
export default polyfill(Banner);
/* replace-end-value */

Banner2.templateStr:
import React from 'react';
import { Button } from 'antd';
import {
  DownOutlined,
} from '@ant-design/icons';
import QueueAnim from 'rc-queue-anim';
import TweenOne, { TweenOneGroup } from 'rc-tween-one';
import BannerAnim, { Element } from 'rc-banner-anim';
/* replace-start-value = import { isImg } from './utils'; */
import { isImg } from '../../../../../share/utils';
/* replace-end-value */
import 'rc-banner-anim/assets/index.css';
/* replace-start */
import './index.less';
/* replace-end */

const BgElement = Element.BgElement;
class Banner extends React.PureComponent {
  render() {
    const { ...props } = this.props;
    const { dataSource, isMobile } = props;
    delete props.dataSource;
    delete props.isMobile;
    const childrenToRender = dataSource.BannerAnim.children.map((item, i) => {
      const elem = item.BannerElement;
      const elemClassName = elem.className;
      delete elem.className;
      const bg = item.bg;
      const textWrapper = item.textWrapper;
      const title = item.title;
      const content = item.content;
      const button = item.button;
      const page = item.page;
      const follow = !isMobile ? {
        delay: 1000,
        minMove: 0.1,
        data: [
          {
            id: `bg${i}`, value: 15, type: 'x',
          },
          { id: `wrapperBlock${i}`, value: -15, type: 'x' },
        ],
      } : null;
      return (
        <Element
          key={i.toString()}
          followParallax={follow}
          {...elem}
          prefixCls={elemClassName}
        >
          <BgElement
            key="bg"
            {...bg}
            id={`bg${i}`}
          />
          <div {...page}>
            <QueueAnim
              type={['bottom', 'top']}
              delay={200}
              key="text"
              {...textWrapper}
              id={`wrapperBlock${i}`}
            >
              <div
                key="logo"
                {...title}
                /* replace-start */
                data-edit="text,image"
              /* replace-end */
              >
                {
                  typeof title.children === 'string' && title.children.match(isImg) ? (
                    <img src={title.children} width="100%" alt="img" />
                  ) : /* replace-start-value = title.children */React.createElement('span', { dangerouslySetInnerHTML: { __html: title.children } })
                  /* replace-end-value */
                }
              </div>
              <div
                key="content"
                {...content}
              >
                {
                  /* replace-start-value = content.children */
                  React.createElement('span', { dangerouslySetInnerHTML: { __html: content.children } })
                  /* replace-end-value */
                }
              </div>
              <Button
                type="ghost"
                key="button"
                {...button}
                /* replace-start */
                data-edit="link,text"
              /* replace-end */
              >
                {
                  /* replace-start-value = button.children */
                  React.createElement('span', { dangerouslySetInnerHTML: { __html: button.children } })
                  /* replace-end-value */
                }
              </Button>
            </QueueAnim>
          </div>
        </Element>
      );
    });
    return (
      <div
        {...props}
        {...dataSource.wrapper}
      >
        <TweenOneGroup
          key="bannerGroup"
          enter={{ opacity: 0, type: 'from' }}
          leave={{ opacity: 0 }}
          component=""
        >
          <BannerAnim
            key="BannerAnim"
            {...dataSource.BannerAnim}
            /* replace-start */
            ref={(c) => {
              this.banner = c;
            }}
          /* replace-end */
          >
            {childrenToRender}
          </BannerAnim>
        </TweenOneGroup>
        <TweenOne
          animation={{
            y: '-=20', yoyo: true, repeat: -1, duration: 1000,
          }}
          className="banner2-icon"
          style={{ bottom: 40 }}
          key="icon"
        >
          <DownOutlined />
        </TweenOne>
      </div>
    );
  }
}

export default Banner;

Banner3.templateStr:
import React from 'react';
import { Button } from 'antd';
import QueueAnim from 'rc-queue-anim';
import Texty from 'rc-texty';
import 'rc-texty/assets/index.css';
/* replace-start */
import './index.less';
/* replace-end */
class Banner extends React.PureComponent {
  render() {
    const { ...currentProps } = this.props;
    const { dataSource } = currentProps;
    delete currentProps.dataSource;
    delete currentProps.isMobile;
    const children = dataSource.textWrapper.children.map((item) => {
      const { name, texty, ...$item } = item;
      if (name.match('button')) {
        return (
          <Button
            type="primary"
            key={name}
            {...$item}
            /* replace-start */
            data-edit="link,text"
            /* replace-end */
          >
            {/* replace-start-value = item.children */
              React.createElement('span', { dangerouslySetInnerHTML: { __html: item.children } })
           /* replace-end-value */}
          </Button>
        );
      }
      /* replace-start */
      if (texty) {
        $item['data-edit'] = 'texty';
      }
      /* replace-end */
      return (
        <div
          key={name}
          {...$item}
        >
          {
            texty
              ? (
                <Texty type="mask-bottom">
                  {item.children}
                </Texty>
              )
              : (
                /* replace-start-value = item.children */
                React.createElement('span', { dangerouslySetInnerHTML: { __html: item.children } })
                /* replace-end-value */
              )
          }
        </div>
      );
    });
    return (
      <div
        {...currentProps}
        {...dataSource.wrapper}
      >
        <QueueAnim
          key="QueueAnim"
          type={['bottom', 'top']}
          delay={200}
          {...dataSource.textWrapper}
        >
          {children}
        </QueueAnim>
      </div>
    );
  }
}
export default Banner;

Banner4.templateStr:
import React from 'react';
import QueueAnim from 'rc-queue-anim';
import TweenOne from 'rc-tween-one';
/* replace-start-value = import { getChildrenToRender } from './utils'; */
import { getChildrenToRender } from '../../../utils';
/* replace-end-value */
/* replace-start */
import './index.less';
/* replace-end */
class Banner4 extends React.PureComponent {
  render() {
    const { ...tagProps } = this.props;
    const { dataSource } = tagProps;
    delete tagProps.dataSource;
    delete tagProps.isMobile;
    const animType = {
      queue: 'bottom',
      one: {
        y: '+=30', opacity: 0, type: 'from', ease: 'easeOutQuad',
      },
    };
    return (
      <div
        {...tagProps}
        {...dataSource.wrapper}
      >
        <div
          {...dataSource.page}
        >
          <QueueAnim
            key="text"
            type={animType.queue}
            leaveReverse
            ease={['easeOutQuad', 'easeInQuad']}
            {...dataSource.childWrapper}
            componentProps={{ md: dataSource.childWrapper.md, xs: dataSource.childWrapper.xs }}
            /* replace-start */
            data-edit="childWrapper"
          /* replace-end */
          >
            {
              dataSource.childWrapper.children.map(getChildrenToRender)
            }
          </QueueAnim>
          <TweenOne
            animation={animType.one}
            key="title"
            {...dataSource.image}
          >
            <img src={dataSource.image.children} width="100%" alt="img" />
          </TweenOne>
        </div>
      </div>
    );
  }
}
export default Banner4;

Banner5.templateStr:
import React from 'react';
import QueueAnim from 'rc-queue-anim';
import TweenOne from 'rc-tween-one';
import OverPack from 'rc-scroll-anim/lib/ScrollOverPack';

import { Tabs, Row, Col } from 'antd';
import { Icon } from '@ant-design/compatible';

import { polyfill } from 'react-lifecycles-compat';
import { getChildrenToRender } from '../../../utils';

import './index.less';

class Banner5 extends React.PureComponent {
  static getDerivedStateFromProps(props, { prevProps, current: prevCurrent }) {
    const { func } = props;
    const nextState = {
      prevProps: props,
    };
    if (prevProps && props !== prevProps) {
      const childLen = props.dataSource.block.children.length;
      if (func) {
        const current = func.currentPage > childLen ? childLen : func.currentPage;
        nextState.current = current;
      } else if (prevCurrent > childLen) {
        nextState.current = childLen;
      }
    }
    return nextState;
  }

  constructor(props) {
    super(props);
    this.state = {
      /* replace-start-value = current: 1 */
      current: props.func ? props.func.currentPage : 1,
      /* replace-end-value */
    };
    this.handleClick = this.handleClick.bind(this);
  }
  handleClick(e) {
     e.preventDefault();
     console.log('The link was clicked.',e.target);

     if (e.target.parentNode != undefined ) {
       var key = e.target.parentNode.dataset.tab
       console.log('Set current = ' + key);

       this.setState({ current: parseFloat(key) });
     } else {
       console.log('e.target.parentNode = underfined ');
     }
   }

  getBlockChildren = (item, i) => {
    const { tag, content } = item;
    const { text, img } = content;
    const textChildren = text.children;
    const { icon } = tag;
    const iconChildren = icon.children;
    const tagText = tag.text;
    return (
      <div
        key={i + 1}
      >
          {
            this.state.current === i + 1 && (
            <Row
              key="content"
              className={content.className}
              gutter={content.gutter}
              {...content}
              data-edit="Row"
            >
              <Col
                className={text.className}
                xs={text.xs}
                md={text.md}
                /* replace-start */
                {...text}
                data-edit={['Col', 'text']}
              /* replace-end */
              >
                {
                  /* replace-start-value = textChildren */
                  React.createElement('span', { dangerouslySetInnerHTML: { __html: textChildren } })
                  /* replace-end-value */
                }
              </Col>
              <Col
                className={img.className}
                xs={img.xs}
                md={img.md}
                /* replace-start */
                {...img}
                data-edit={['Col', 'image']}
              /* replace-end */
              >
                <img src={img.children} width="100%" alt="img" />
              </Col>
            </Row>
          )}
      </div>
    );
  }

  getTabChildren = (item, i) => {
    const { title, image } = item;
    return (
      <div
        {...item}
        data-tab={i+1}
        onClick={this.handleClick}
        >
         <div className="bannerStyle8" ></div>
         <span
           {...title}
            data-edit="texty"
            className={this.state.current === i + 1 ? "bannerStyle13" : "bannerStyle9" }
            lineheightoptimizer="function(e){return Math.ceil(9.24*Math.pow(10,-5)*Math.pow(e,2)+1.492*e+2.174)}" >
            {title.children}
          </span>
           <img
             className="bannerStyle10"
             src={image.children}
             quality="original" />
      </div>
    );
  }
  render() {
    const { ...currentProps } = this.props;
    const { dataSource } = currentProps;
    delete currentProps.dataSource;
    delete currentProps.isMobile;
    const tabs = dataSource.tabs.children.map(this.getTabChildren);
    const tabsChildren = dataSource.block.children.map(this.getBlockChildren);
    const { banner } =  dataSource;
    const { image, title } = banner;

    return (
      <div
        {...currentProps}
        {...dataSource.wrapper}

        data-comp={[`tabs-switch={ "current": ${
          this.state.current}, "total": ${dataSource.block.children.length
        } ,"childRoute": ["block"] }`]}
      >
          <div className="bannerStyle0">
              <div
                  {...banner}
                  data-edit="image"
                  >
                 <img
                   className={dataSource.banner.image.className}
                   src={dataSource.banner.children}
                   quality="original"
                   data-sy="mnbvc" />
                   <div className="bannerStyle3" >
                      <span
                        {...title}
                        data-edit="texty"
                        lineheightoptimizer="function(e){return Math.ceil(9.24*Math.pow(10,-5)*Math.pow(e,2)+1.492*e+2.174)}"
                        >
                        { title.children}
                      </span>
                      <div
                        className="bannerStyle5"
                        id="header"
                         >
                         {tabs}
                      </div>
                   </div>
              </div>
          </div>
    </div>
    );
  }
}
export default polyfill(Banner5);

