      const childrenItem = childRow.map(($item, i) => {
        const { children: colChild, arrow, ...colProps } = $item;
        const { ...childProps } = colChild;
        return (
          <Col
            {...colProps}
            key={i.toString()}
            /* replace-start */
            data-edit="Col"
          /* replace-end */
